(function (a) {
    "use strict";
    a(document).on("click", "a.page-scroll", function (e) {
        var l = a(this);
        a("html, body").stop().animate({scrollTop: a(l.attr("href")).offset().top - 50}, 1250, "easeInOutExpo"), e.preventDefault()
    })

    a("body").scrollspy({
        target: ".navbar-fixed-top",
        offset: 51
    })

    a(".navbar-collapse ul li a").click(function () {
        a(".navbar-toggle:visible").click()
    })

    a("#mainNav").affix({
        offset: {
            top: 100
        }
    })

    window.sr = ScrollReveal();
    sr.reveal('.sr-icons', {
        duration: 600,
        scale: .3,
        distance: "0px",
        viewFactor: 0.9
    }, 200);
    sr.reveal('.sr-button', {
        duration: 600,
        scale: .3,
        distance: "0px",
        viewFactor: 0.9
    });
    sr.reveal('.sr-contact', {
        duration: 500,
        scale: .3,
        distance: "0px",
        viewFactor: 0.8
    }, 300);
})(jQuery);

sr.sync();